import React, { useEffect, useState } from "react";

const Rating = ({ bulletsQuantity = 5 }) => {
  const bulletsArray = Array.from({length: bulletsQuantity});
  const [selectedBullet, setSelectedBullet] = useState(-1);



  const printBullets = () => {
    const printedBullets = bulletsArray.map((bullet) => {
      return (
        <span
          key={bullet.id}
          onClick={() => setSelectedBullet(bullet.id)}
          className={`border border-cyberPink p-2 rounded ${
            selectedBullet >= bullet.id ? "bg-cyberPink" : ""
          }`}
        />
      );
    });
    return printedBullets;
  };

  return <div className="flex gap-2">{printBullets()}</div>;
};

export default Rating;
