import { http } from "../plugins/http/http";
export const moviesServices = () => ({
  getMovies: async () => {
    let movieData = [];

    await http.get("/movies").then((data) => {
      movieData = data;
    });
    return movieData;
  },

  getMovieComments: async (id) => {
    let movieComments = [];

    await http.get(`/movies/${id}/comments`).then((data) => {
      console.log("test kimo data", data);
    });

    return movieComments;
  },

  postComment: async (id, comment) => {
    await http.post(`/movies/${id}/postComment`, comment).then((response) => {
      console.log("test kimo res", response);
    });
  },
});
