import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

import "./index.css";

import Auth from "./views/Auth/Auth";
import Home from "./views/Home";
import Comments from "./views/Comments/Comments";

function App() {
  const userIsLogged = localStorage.getItem("token");

  const PrivateRoute = ({ element, redirectTo, ...props }) => {
    return (
      <Route
        {...props}
        element={userIsLogged ? element : <Navigate to={redirectTo} />}
      />
    );
  };
  return (
    <Router>
      <Routes>
        <Route
          path="/auth"
          element={!userIsLogged ? <Auth /> : <Navigate to="/" />}
        />

        <PrivateRoute path="/" element={<Home />} redirectTo="/auth" />

        <PrivateRoute
          path="/comments/:id"
          element={<Comments />}
          redirectTo="/auth"
        />
      </Routes>
    </Router>
  );
}

export default App;
