import React from "react";

const Button = ({ text, onClick }) => {
  const handleClick = () => {
    if (onClick) onClick();
  };

  return <div className=" bg-cyberPurple p-2 pl-8 pr-8 rounded" onClick={handleClick}><p className="font-bold">{text}</p></div>;
};

export default Button;
