import React from "react";

const Input = ({ type = "text", handleChange, inputValue, placeholder }) => {
  const onChange = (e) => {
    if (handleChange) handleChange(e.target.value);
  };

  return (
    <>
      <div className="appearance-none border border border-pink rounded p-1 w-3/4">
        <input
          className="outline-0 w-full bg-gray-50 "
          type={type}
          onChange={(e) => onChange(e)}
          value={inputValue}
          style={{ background: "none" }}
          placeholder={placeholder}
        />
      </div>
    </>
  );
};

export default Input;
