import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

import "./index.css";

import Auth from "./views/Auth/Auth";
import Home from "./views/Home";
import Comments from "./views/Comments/Comments";

function App() {
  const userIsLogged = localStorage.getItem("token");

  const PrivateRoute = ({ children, redirectTo, ...props }) => {
    console.log("tyest kimo props", props);
    return (
      <Route
        {...props}
        element={userIsLogged ? children : <Navigate to={redirectTo} />}
      />
    );
  };
  return (
    <Router>
      <Routes>
        <Route
          path="/auth"
          element={!userIsLogged ? <Auth /> : <Navigate to="/" />}
        />

        <PrivateRoute path="/" redirectTo="/auth">
          <Route index element={<Home />} />
          <Route path="comments/:id" element={<Comments />} />
        </PrivateRoute>
      </Routes>
    </Router>
  );
}

export default App;
