import React from "react";
import Button from "../components/Button/Button";

const Home = () => {
  const moviesData = [
    {
      id: "mov-1",
      title: "Star Trek: First Contact",
      synopsis:
        "The Borg travel back in time intent on preventing Earth's first contact with an alien species. Captain Picard and his crew pursue them to ensure that Zefram Cochrane makes his maiden flight reaching warp speed.",
      cover:
        "https://upload.wikimedia.org/wikipedia/en/0/01/Star_trek_first_contact_poster.jpg",
      rateList: [
        {
          userId: "usr-1",
          rate: 5,
        },
      ],
      averageRate: 5,
      comments: [
        {
          userId: "usr-1",
          comment: "Nice movie",
        },
      ],
    },
  ];

  const printMovieList = () => {
    if (!moviesData.length) return;
    else {
      const movieList = moviesData?.map((movie) => {
        return (
          <div  className="p-4"key={movie.id}>
            <img src={movie.cover}></img>
            <div>
              <p className="font-bold text-xl">{movie.title}</p>
              <p>{movie.synopsis}</p>
              <p>{movie.averageRate}</p>
              <div>
                <Button text="Show comments" />
              </div>
            </div>
          </div>
        );
      });
      return movieList;
    }
  };

  return (
    <div>
      <h2>Movie Database</h2>
      <div>{printMovieList()}</div>
    </div>
  );
};

export default Home;
