import { http } from "../plugins/http/http";

const saveUser = (user) => {
  window.localStorage.setitem("token", user._id);
};
export const authServices = () => ({
  login: async (body) => {
    let returnValue = "";
    console.log("test kimo body", body);
    await http
      .post("/login", body)
      .then((user) => {
        saveUser(user);
        console.log("test kimo user", user);
      })
      .catch((err) => {
        console.error(err);
        if (err.message) returnValue = err?.message;
        else
          returnValue =
            "There was an error while trying to login. Try it again in a few minutes";
      });
    return returnValue;
  },
});
