import React, { useState } from "react";
import Button from "../components/Button/Button";

const Home = () => {
  const [selectedMovie, setSelectedMovie] = useState("");
  const [movieComments, setMovieComments] = useState([]);

  const moviesData = [
    {
      id: "mov-1",
      title: "Star Trek: First Contact",
      synopsis:
        "The Borg travel back in time intent on preventing Earth's first contact with an alien species. Captain Picard and his crew pursue them to ensure that Zefram Cochrane makes his maiden flight reaching warp speed.",
      cover:
        "https://upload.wikimedia.org/wikipedia/en/0/01/Star_trek_first_contact_poster.jpg",
      rateList: [
        {
          userId: "usr-1",
          rate: 5,
        },
      ],
      averageRate: 5,
      comments: [
        {
          userId: "usr-1",
          userName: "Pepe",
          comment: "Nice movie",
        },
      ],
    },
    {
      id: "mov-2",
      title: "Event Horizon",
      synopsis:
        "A rescue crew is tasked with investigating the mysterious reappearance of a spaceship that had been lost for seven years.",
      cover:
        "https://upload.wikimedia.org/wikipedia/en/8/8c/Event_horizon_ver1.jpg",
      rateList: [
        {
          userId: "usr-1",
          rate: 4,
        },
        {
          userId: "usr-2",
          rate: 5,
        },
      ],
      averageRate: 4.5,
      comments: [
        {
          id: "com-1",
          userId: "usr-1",
          userName: "Pepe",
          comment:
            "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent scelerisque tempus erat. Pellentesque porttitor fermentum elit, nec facilisis metus molestie id. Sed bibendum eleifend porta. Curabitur et luctus leo. Maecenas et mauris mattis felis hendrerit feugiat. Mauris ut gravida sapien. Integer mattis, mauris id pellentesque sagittis, nibh odio euismod dui, vel congue elit justo at magna. Pellentesque eget porta felis. Vivamus eleifend sem augue, sit amet viverra lacus varius id. Integer augue tortor, elementum id feugiat quis, feugiat finibus diam. Curabitur sit amet sem nec orci lacinia mollis. Praesent gravida sodales dolor, tempor efficitur nisi consectetur id. Nunc id urna vel nibh facilisis mollis. Phasellus vulputate, nibh quis pretium consectetur, nibh sem bibendum metus, sed lacinia lacus leo at nibh. Nulla facilisi.",
        },
      ],
    },
  ];

  const handleShowComments = (movie) => {
    setSelectedMovie(movie.id);
    setMovieComments(movie.comments);
  };

  const printMovieList = () => {
    if (!moviesData.length) return;
    else {
      const movieList = <div>
        { moviesData?.map((movie) => {
        return (
          <div
            className="p-4 bg-cyberDarkBlue border-2 border-cyberBlue rounded-lg"
            key={movie.id}
          >
            <div className="flex justify-center items-center">
              <img className="w-150" src={movie.cover}></img>
            </div>
            <div>
              <p className="font-bold text-xl text-cyberPink pt-4 pb-4">
                {movie.title}
              </p>
              <p>{movie.synopsis}</p>
              <div className="flex justify-end gap-28 p-4 items-center">
                <div className="border border-cyberTurquoise rounded-full flex justify-center pl-4 pr-4 pt-1.5 pb-1.5">
                  <p className="text-3xl ">{movie.averageRate}</p>
                </div>
                <Button
                  text="Show comments"
                  onClick={() => handleShowComments(movie)}
                />
              </div>
            </div>
          </div>
        );
      })}
      </div>
      return movieList;
    }
  };

  const printCommentList = () => {
    if (!movieComments) {
      return <div>No comments yet.</div>;
    } else {
      const commentsList = (
        <div>
          {movieComments.map((comment) => {
            return (
              <div
                className="bg-cyberDarkBlue border-2 border-cyberBlue absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-100% h-fit"
                index={comment.id}
              >
                <p>Comment by: {comment.userName}</p>
                {comment.comment}
              </div>
            );
          })}
        </div>
      );
      return commentsList;
    }
  };

  return (
    <div className="relative h-3/4 ">
      <h2>Movie Database</h2>
      <div className="p-2 flex flex-col gap-4 overflow-scroll">
        {printMovieList()}
      </div>
      <div>{printCommentList()}</div>
    </div>
  );
};

export default Home;
