import React, { useState } from "react";
import Input from "../Input/Input";
import Button from "../Button/Button";
const LoginForm = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const handleFormChange = (value, type) => {
    setFormData((data) => ({
      ...data,
      [type]: value,
    }));
    console.log("test kimo value", value, type);
  };

  const login = () => {
    console.log("test kimo hola");
  };
  return (
    <div className="flex flex-col border-2 border-cyberBlue rounded-xl items-center w-3/4 pt-16 pb-8  shadow-lg  shadow-cyberBlue gap-8 bg-cyberDarkBlue">
      <Input
        type="text"
        handleChange={(value) => handleFormChange(value, "email")}
        placeholder="E-mail"
      />
      <Input
        type="password"
        handleChange={(value) => handleFormChange(value, "password")}
        placeholder="Password"
      />
      <div className="pt-8">
        <Button text="Login" onClick={login} />
      </div>
    </div>
  );
};

export default LoginForm;
