import React from "react";
import Button from "../components/Button/Button";

const Home = () => {
  const moviesData = [
    {
      id: "mov-1",
      title: "Star Trek: First Contact",
      synopsis:
        "The Borg travel back in time intent on preventing Earth's first contact with an alien species. Captain Picard and his crew pursue them to ensure that Zefram Cochrane makes his maiden flight reaching warp speed.",
      cover:
        "https://upload.wikimedia.org/wikipedia/en/0/01/Star_trek_first_contact_poster.jpg",
      rateList: [
        {
          userId: "usr-1",
          rate: 5,
        },
      ],
      averageRate: 5,
      comments: [
        {
          userId: "usr-1",
          comment: "Nice movie",
        },
      ],
    },
    {
        id: "mov-2",
        title: "Event Horizon",
        synopsis:
          "The Borg travel back in time intent on preventing Earth's first contact with an alien species. Captain Picard and his crew pursue them to ensure that Zefram Cochrane makes his maiden flight reaching warp speed.",
        cover:
          "https://pics.filmaffinity.com/event_horizon-152495994-large.jpg",
        rateList: [
          {
            userId: "usr-1",
            rate: 4,
          },
          {
            userId: "usr-2",
            rate: 4.5,
          },
        ],
        averageRate: 5,
        comments: [
          {
            userId: "usr-1",
            comment: "Nice movie",
          },
        ],
      },
  ];

  const printMovieList = () => {
    if (!moviesData.length) return;
    else {
      const movieList = moviesData?.map((movie) => {
        return (
          <div
            className="p-4 bg-cyberDarkBlue border-2 border-cyberBlue rounded-lg"
            key={movie.id}
          >
            <div className="flex justify-center items-center">
              <img className="w-150"src={movie.cover}></img>
            </div>
            <div>
              <p className="font-bold text-xl pt-4 pb-4">{movie.title}</p>
              <p>{movie.synopsis}</p>
              <div className="flex justify-end gap-28 p-4 items-center">
                <div className="border border-cyberTurquoise rounded-full flex justify-center pl-4 pr-4 pt-1.5 pb-1.5">
                  <p className="text-3xl ">{movie.averageRate}</p>
                </div>

                <Button text="Show comments" />
              </div>
            </div>
          </div>
        );
      });
      return movieList;
    }
  };

  return (
    <div>
      <h2>Movie Database</h2>
      <div className="p-2">{printMovieList()}</div>
    </div>
  );
};

export default Home;
